.road {
    height: 160px;
    width: 100%;
    /*background-image: url(assets/road.jpg);
    background-size: contain;*/
    background-color: grey;
    position: relative;
    margin-top: 20px;
}
.road::after {
    content: " ";
    display: inline-block;
    border-bottom: 10px dashed yellow;
    position: relative;
    width: 100%;
    height: 50%;
}
.car {
    position: absolute;
    /*top: 165px;*/
    top: 10px;
    left: 50%;
    height: 60px;
}
/* This prevents the scrollbar when the car is off screen by putting the road and car inside a container that is the entire 
 * screen width and height, but has overflow hidden
 */
.wrapper {
    position: relative;
    width: 100%;
    height: 200px;
    overflow: hidden;
    background: green;
}

#score {
    left: 0;
    top: 0;
}
#clock {
    right: 0;
    top: 0;
}

.display {
    position: absolute;
    padding: 4px 10px;
    height: 20px;
    font-size: 16px;
    background: #030303;
    text-align: center;
    color: #f0f0f0;
}

